import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Swipeout from 'react-native-swipeout';


export default function List(props){

    const obj = props.todos ? Object.entries(props.todos) : null;
    const items = [];
    const swipeSettings = {
        autoClose : true,
        onClose: (secId, rowId, direction)=>{

        },
        onOpen: (secId, rowId, direction)=>{

        },
        right: [
            {
                onPress: ()=>{

                },
                text: 'Delete', type: 'delete'
            }
        ]

    }

    if(obj != null){
        obj.forEach(([key, value]) => {
                items.push(<Swipeout {...swipeSettings} key={key}>
                            <Text onPress={props.onRemove.bind(this, key)} 
                                style={styles.items}>{value.text}</Text></Swipeout>)
        });
    }else{
        items.push(<Text  key={1} style={styles.noItem} >NO ITEM</Text>)
    }

 return(
  <View  style={styles.container}>
      
      { items }
     
  </View>
 );
}

const styles = StyleSheet.create({
    container: {
        paddingTop: 10
    },
    items: {
        width: '100%',
        padding: 10,
        backgroundColor: '#FFF',
        marginVertical: 3,
        borderRadius: 5,
    },
    noItem : {
        width: '100%',
        padding: 10,
        color: 'red',
        marginVertical: 2,
        borderRadius: 5,
        textAlign: 'center' 
    }
})
