import React from 'react';
import { StyleSheet, Text, View } from 'react-native';

export default function List(props){

    const obj = props.todos ? Object.entries(props.todos) : null;
    const items = [];

    if(obj != null){
        obj.forEach(([key, value]) => {
                items.push(<Text onPress={props.onRemove.bind(this, key)} 
                                style={styles.items} 
                                key={key}>{value.text}</Text>)
        });
    }else{
        items.push(<Text  key={1} style={styles.noItem} >NO ITEM</Text>)
    }

 return(
  <View  style={styles.container}>
      { items }
  </View>
 );
}

const styles = StyleSheet.create({
    container: {

    },
    items: {
        width: '100%',
        padding: 10,
        backgroundColor: '#FFF',
        marginVertical: 2,
        borderRadius: 5
    },
    noItem : {
        width: '100%',
        padding: 10,
        color: 'red',
        marginVertical: 2,
        borderRadius: 5,
        textAlign: 'center' 
    }
})
