import React, { useState} from 'react';
import { StyleSheet, Text, View, Button, TextInput, TouchableOpacity} from 'react-native';

export default function TextField(props){
 return(
  <View style={styles.container}>
      <TextInput 
        style={styles.input}
      />
      <TouchableOpacity
        style={styles.addBtn}
        onPress={props.onAddTodo.bind('scale')}
      >
          <Text style={styles.addBtnTxt}>Button</Text>
      </TouchableOpacity>
  </View>
 );
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFF',
        flex: 1,
        padding: 10
    },
    addBtn: {
        backgroundColor: 'green',
        borderRadius: 5,
        paddingVertical: 8,
        maxWidth: '100%'
    },
    addBtnTxt: {
        color: '#FFF',
        textAlign: 'center'
    },
    input: {
        borderWidth: 1,
        borderColor: '#e1e1e1',
        padding: 8,
        borderRadius: 5,
        marginBottom: 10,
    }

})
