import React, { useEffect } from 'react';
import { StyleSheet, Text, SafeAreaView, TouchableOpacity  } from 'react-native';
import Swipeable from 'react-native-gesture-handler/Swipeable';
import posed from 'react-native-pose';
// import { Gesturehandler } from 'expo';


export default function List(props){

    const obj = props.todos ? Object.entries(props.todos) : null;
    const items = [];
    let rowRefs = new Map();

    const swipeOpen = (progress, key) =>{
        setTimeout(()=>{
            props.onRemove(key)
        }, 1000)
    }

    const swipeRight = () =>{

        return (
            <TouchableOpacity  
              style={{ flex: 1,
                        backgroundColor: 'firebrick', 
                        justifyContent: 'center', 
                        alignItems: 'flex-end',
                        paddingVertical: 10,
                        paddingHorizontal: 13,
                        marginVertical: 5, 
                        borderRadius: 20}}>
              <Text
                style={{
                  color: 'white',
                  paddingHorizontal: 10,
                  fontWeight: '600'
                }}>
                Delete
              </Text>
            </TouchableOpacity >
          );

    }


    if(obj != null){
        obj.forEach(([key, value]) => {
                items.push(<Swipeable 
                    friction={2}
                    onSwipeableOpen={(progress) => swipeOpen(progress, key)}
                            renderRightActions={swipeRight} key={key}>
                            <Text  
                                style={[styles.items, {textDecorationLine: value.isCompleted ? 'line-through': 'none'}]}>{value.text}
                                </Text></Swipeable >)
        });
    }else{
        items.push(<Text  key={1} style={styles.noItem} >NO ITEM</Text>)
    }

 return(
  <SafeAreaView  style={styles.container}>
      
      { items }
     
  </SafeAreaView>
 );
}

const styles = StyleSheet.create({
    container: {
        paddingTop: 10
    },
    items: {
        width: '100%',
        paddingVertical: 10,
        paddingHorizontal: 15,
        backgroundColor: '#FFF',
        marginVertical: 5,
        borderRadius: 20,
        shadowColor: "#646464",
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        
        elevation: 5,
    },
    noItem : {
        width: '100%',
        padding: 10,
        color: 'red',
        marginVertical: 2,
        borderRadius: 5,
        textAlign: 'center' 
    }
})
