import Swipeable from 'react-native-gesture-handler/Swipeable';
export default class MySwipeable extends Swipeable {
    closeInstantlyLeft = () => {
      const { onSwipeableWillClose, onSwipeableClose } = this.props;
      const { dragX, rowTranslation } = this.state;
      dragX.setValue(0);
      rowTranslation.setValue(0);
      this.setState({ rowState: Math.sign(0) });
      if (onSwipeableWillClose) {
        onSwipeableWillClose();
      }
      if (onSwipeableClose) {
        onSwipeableClose();
      }
    };
    closeInstantlyRight = () => {
      const { rowWidth } = this.state;
      this.setState({ rowState: Math.sign(rowWidth) });
      this.closeInstantlyLeft()
    };
  }
