import React, { useEffect } from 'react';
import { StyleSheet, Text, SafeAreaView, TouchableOpacity  } from 'react-native';
import Swipeable from 'react-native-gesture-handler/Swipeable';
// import { Gesturehandler } from 'expo';


export default function List(props){

    const obj = props.todos ? Object.entries(props.todos) : null;
    const items = [];

    const swipeRight = (progress, key) =>{
        return (
            <TouchableOpacity  onPress={()=> {
                props.onRemove(key)
                // onPress={props.onRemove.bind(this, key)}
            }}
              style={{ flex: 1,
                        backgroundColor: 'firebrick', 
                        justifyContent: 'center', 
                        padding: 10,
                        marginVertical: 3, 
                        borderRadius: 5}}>
              <Text
                style={{
                  color: 'white',
                  paddingHorizontal: 10,
                  fontWeight: '600'
                }}>
                Delete
              </Text>
            </TouchableOpacity >
          )
    }


    if(obj != null){
        obj.forEach(([key, value]) => {
                items.push(<Swipeable renderRightActions={(progress) => swipeRight(progress, key)} key={key}>
                            <Text  
                                style={[styles.items, {textDecorationLine: value.isCompleted ? 'line-through': 'none'}]}>{value.text}
                                </Text></Swipeable >)
        });
    }else{
        items.push(<Text  key={1} style={styles.noItem} >NO ITEM</Text>)
    }

 return(
  <SafeAreaView  style={styles.container}>
      
      { items }
     
  </SafeAreaView>
 );
}

const styles = StyleSheet.create({
    container: {
        paddingTop: 10
    },
    items: {
        width: '100%',
        padding: 10,
        backgroundColor: '#FFF',
        marginVertical: 3,
        borderRadius: 5,
    },
    noItem : {
        width: '100%',
        padding: 10,
        color: 'red',
        marginVertical: 2,
        borderRadius: 5,
        textAlign: 'center' 
    }
})
